import React from "react";
import Notification from "./Notification";

const reserveNotifications = [
    { message: "안녕" },
    { message: "점심 먹을 시간이야" },
    { message: "셋셀동안 일어나라" },
];

var timer;

class NotificationList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            notifications: [], // 상태 이름을 notifications로 변경
        };
    }

    componentDidMount() { // 오타 수정
        timer = setInterval(() => {
            const { notifications } = this.state;
            if (notifications.length < reserveNotifications.length) {
                const index = notifications.length;
                // 새로운 배열 생성을 통해 불변성을 보장하고 상태를 업데이트
                this.setState({
                    notifications: notifications.concat(reserveNotifications[index]),
                });
            } else {
                clearInterval(timer);
            }
        }, 1000);
    }

    render() {
        return (
            <div>
                {this.state.notifications.map((notification) => { // 상태 이름 수정
                    return <Notification message={notification.message} />;
                })}
            </div>
        );
    }
}

export default NotificationList;
