import React, { useRef, useState } from "react";

const UseRefEx=()=>{
    const [state, setState] = useState(0);
    const ref = useRef(0);
    const increaseState=()=>{
        setState(state+1);
        setState((state)=>{
            return state+1;
        });
    };
    const increaseRef=()=>{
        ref.current = ref.current+2;
    };
    return(
        <div>
            <p>현제 state 값: {state}</p>
            <p>현제 useref값: {ref.current}</p>
            <button onClick={increaseState}>클릭</button>
            <button onClick={increaseRef}>클릭</button>
        </div>
    );
};

//state값이 변화가 되면 랜더링이 새로 되는데. 값이 변화해도 랜더링이 필요없는 작업을 수행할때 사용을 한다.

export default UseRefEx;
