import React from "react";

const styles={
    wrapper:{
        margin: 0,
        padding: 8,
        display: "flex",
        flexDirection:"row",
        border:"1px solid black",
        borderRadius:16,
    },
    imageContainer:{},
    image:{
        width:50,
        height:50,
        borderRadius:25
    },
    contentContainer:{
        marginLeft: 8,
        display:"flex",
        flexDirection:"column",
        justifyContent:"center",
    },
    nameText:{
        color:"black",
        fontSize:16,
        fontWeight:"bold",
    },
    commentText:{
        color:"black",
        fontSize:16,
    },

};

function Comment(props){
    return(
        <div style={styles.wrapper}>
            <div style={styles.imageContainer}>
                <img                 
                src=".../"
                style={styles.image}
                />
            </div>

            <div>
                <span style={styles.nameText}>기무성</span>
                <span style={styles.commentText}>아 배고파 ㅋㅋ</span>
            </div>
        </div>
    );
}

export default Comment;
